pragma solidity 0.8.0;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract Marketplace is ReentrancyGuard, Ownable {
    using Counters for Counters.Counter;
    struct Offering {
        uint256 offeringId;
        address nftContract;
        uint256 tokenId;
        address seller;
        uint256 price;
        bool closed;
    }
    Counters.Counter private _nonce;
    Counters.Counter private _openningCounter;
    IERC20 public immutable token;
    uint256 public listingPrice;
    mapping(uint256 => Offering) private idToOffering;

    event OfferingCreated(
        uint256 indexed offeringId,
        address indexed nftContract,
        uint256 indexed tokenId,
        address seller,
        uint256 price,
        bool closed
    );
    event OfferingClosed(uint256 indexed offeringId);
    event OfferingPriceChanged(uint256 indexed offeringId, uint256 newPrice);
    event Receive(address indexed from, uint256 indexed amount);
    event ChangeListingPrice(uint256 newPrice);

    constructor(IERC20 _token, uint256 _listingPrice) {
        token = _token;
        listingPrice = _listingPrice;
    }

    modifier onlySeller(uint256 offeringId) {
        address seller = idToOffering[offeringId].seller;
        require(seller == _msgSender(), "Only offering's owner allowed");
        _;
    }

    modifier whenNotClosed(uint256 offeringId) {
        bool closed = idToOffering[offeringId].closed;
        require(closed == false, "Offering closed");
        _;
    }

    function changeListingPrice(uint256 newPrice) external onlyOwner {
        listingPrice = newPrice;
        emit ChangeListingPrice(listingPrice);
    }

    function offer(
        address nftContract,
        uint256 tokenId,
        uint256 price
    ) external nonReentrant returns (bool) {
        require(price > 0, "Price must be positive");
        _payOwnerListingPrice();
        IERC721(nftContract).transferFrom(_msgSender(), address(this), tokenId);
        _nonce.increment();
        _openningCounter.increment();
        uint256 offeringId = _nonce.current();
        idToOffering[offeringId] = Offering(
            offeringId,
            nftContract,
            tokenId,
            _msgSender(),
            price,
            false
        );
        emit OfferingCreated(
            offeringId,
            nftContract,
            tokenId,
            _msgSender(),
            price,
            false
        );
        return true;
    }

    function cancelOffering(uint256 offeringId)
        external
        nonReentrant
        onlySeller(offeringId)
        whenNotClosed(offeringId)
        returns (bool)
    {
        idToOffering[offeringId].closed = true;
        _openningCounter.decrement();
        uint256 tokenId = idToOffering[offeringId].tokenId;
        address nftContract = idToOffering[offeringId].nftContract;
        IERC721(nftContract).transferFrom(address(this), _msgSender(), tokenId);
        emit OfferingClosed(offeringId);
        return true;
    }

    function changeOfferingPrice(uint256 offeringId, uint256 newPrice)
        external
        nonReentrant
        onlySeller(offeringId)
        whenNotClosed(offeringId)
        returns (bool)
    {
        idToOffering[offeringId].price = newPrice;
        emit OfferingPriceChanged(offeringId, newPrice);
        return true;
    }

    /* Creates the sale of a marketplace item */
    /* Transfers ownership of the item, as well as funds between parties */
    function buyOffering(uint256 offeringId)
        external
        nonReentrant
        whenNotClosed(offeringId)
        returns (bool)
    {
        uint256 price = idToOffering[offeringId].price;
        uint256 tokenId = idToOffering[offeringId].tokenId;
        address seller = idToOffering[offeringId].seller;
        address nftContract = idToOffering[offeringId].nftContract;
        bool success = token.transferFrom(_msgSender(), seller, price);
        require(success, "Marketplace: Can not pay seller for the offering");
        IERC721(nftContract).transferFrom(address(this), _msgSender(), tokenId);
        idToOffering[offeringId].closed = true;
        _openningCounter.decrement();
        emit OfferingClosed(offeringId);
        return true;
    }

    function _payOwnerListingPrice() internal {
        bool success = token.transferFrom(_msgSender(), owner(), listingPrice);
        require(
            success,
            "Marketplace: Can not pay owner for the listing price"
        );
    }

    /* receive ether function */
    receive() external payable {
        emit Receive(_msgSender(), msg.value);
    }

    /* let owner withdraw ether from contract */
    function withdraw() external onlyOwner returns (bool) {
        uint256 balance = address(this).balance;
        payable(owner()).transfer(balance);
        return true;
    }

    /* Returns all openning offerings */
    function fetchOpenOfferings() public view returns (Offering[] memory) {
        uint256 itemCount = _nonce.current();
        uint256 offeringCount = _openningCounter.current();
        uint256 currentIndex = 0;
        Offering[] memory items = new Offering[](offeringCount);
        for (uint256 i = 0; i < itemCount; i++) {
            if (idToOffering[i + 1].closed == false) {
                uint256 currentId = i + 1;
                Offering storage currentOffering = idToOffering[currentId];
                items[currentIndex] = currentOffering;
                currentIndex++;
            }
        }
        return items;
    }

    function fetchOffering(uint256 offeringId)
        public
        view
        returns (Offering memory)
    {
        Offering memory item = idToOffering[offeringId];
        return item;
    }

    function fetchOfferingsFrom(address seller)
        public
        view
        returns (Offering[] memory)
    {
        uint256 total = _nonce.current();
        uint256 itemCount = 0;
        uint256 currentIndex = 0;

        for (uint256 i = 0; i < total; i++) {
            if (idToOffering[i + 1].seller == seller) {
                itemCount++;
            }
        }

        Offering[] memory items = new Offering[](itemCount);

        for (uint256 i = 0; i < itemCount; i++) {
            if (idToOffering[i + 1].seller == seller) {
                uint256 currentId = i + 1;
                Offering storage currentOffering = idToOffering[currentId];
                items[currentIndex] = currentOffering;
                currentIndex++;
            }
        }
        return items;
    }
}
