pragma solidity 0.8.0;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

contract Marketplace is ReentrancyGuard, Ownable {
    using Counters for Counters.Counter;
    using SafeMath for uint256;
    struct Offer {
        uint256 offerId;
        IERC721 hostContract;
        uint256 tokenId;
        address seller;
        uint256 price;
        bool closed;
    }
    Counters.Counter private _nonce;
    Counters.Counter private _openning;
    IERC20 public immutable token;

    // fee in percent so that 100% is 10000
    // initial fee is 3% => 300
    uint16 public listingFee;
    mapping(uint256 => Offer) private idToOffer;

    event OfferCreated(
        uint256 indexed offerId,
        IERC721 indexed hostContract,
        uint256 indexed tokenId,
        address seller,
        uint256 price,
        bool closed
    );
    event OfferClosed(uint256 indexed offerId);
    event OfferPriceChanged(uint256 indexed offerId, uint256 newPrice);
    event Receive(address indexed from, uint256 indexed amount);
    event ChangeListingFee(uint256 fee);

    constructor(IERC20 _token, uint16 _listingFee) {
        token = _token;
        listingFee = _listingFee;
    }

    modifier onlySeller(uint256 offerId) {
        address seller = idToOffer[offerId].seller;
        require(seller == _msgSender(), "Only seller allowed");
        _;
    }

    modifier whenNotClosed(uint256 offerId) {
        bool closed = idToOffer[offerId].closed;
        require(closed == false, "Offer closed");
        _;
    }

    function changeListingFee(uint16 fee) external onlyOwner {
        require(fee <= 3000, "Fee must less than or equal to 30%");
        listingFee = fee;
        emit ChangeListingFee(listingFee);
    }

    function offer(
        IERC721 hostContract,
        uint256 tokenId,
        uint256 price
    ) external nonReentrant returns (bool) {
        require(price > 0, "Price must be positive");
        _payFee(price);
        hostContract.transferFrom(_msgSender(), address(this), tokenId);
        _nonce.increment();
        _openning.increment();
        uint256 offerId = _nonce.current();
        idToOffer[offerId] = Offer(
            offerId,
            hostContract,
            tokenId,
            _msgSender(),
            price,
            false
        );
        emit OfferCreated(
            offerId,
            hostContract,
            tokenId,
            _msgSender(),
            price,
            false
        );
        return true;
    }

    function cancel(uint256 offerId)
        external
        nonReentrant
        onlySeller(offerId)
        whenNotClosed(offerId)
        returns (bool)
    {
        idToOffer[offerId].closed = true;
        _openning.decrement();
        uint256 tokenId = idToOffer[offerId].tokenId;
        IERC721 hostContract = idToOffer[offerId].hostContract;
        hostContract.transferFrom(address(this), _msgSender(), tokenId);
        emit OfferClosed(offerId);
        return true;
    }

    function changePrice(uint256 offerId, uint256 newPrice)
        external
        nonReentrant
        onlySeller(offerId)
        whenNotClosed(offerId)
        returns (bool)
    {
        idToOffer[offerId].price = newPrice;
        emit OfferPriceChanged(offerId, newPrice);
        return true;
    }

    /* Creates the sale of a marketplace item */
    /* Transfers ownership of the item, as well as funds between parties */
    function buy(uint256 offerId)
        external
        nonReentrant
        whenNotClosed(offerId)
        returns (bool)
    {
        uint256 price = idToOffer[offerId].price;
        uint256 tokenId = idToOffer[offerId].tokenId;
        address seller = idToOffer[offerId].seller;
        IERC721 nftContract = idToOffer[offerId].hostContract;
        bool success = token.transferFrom(_msgSender(), seller, price);
        require(success, "Marketplace: Can not pay seller for the offering");
        nftContract.transferFrom(address(this), _msgSender(), tokenId);
        idToOffer[offerId].closed = true;
        _openning.decrement();
        emit OfferClosed(offerId);
        return true;
    }

    function _payFee(uint256 amount) internal {
        uint256 fee = calculateFee(amount);
        bool success = token.transferFrom(_msgSender(), owner(), fee);
        require(
            success,
            "Marketplace: Can not pay owner for the listing price"
        );
    }

    function calculateFee(uint256 amount) public view returns (uint256) {
        return amount.mul(listingFee).div(10000);
    }

    /* receive ether function */
    receive() external payable {
        emit Receive(_msgSender(), msg.value);
    }

    /* let owner withdraw ether from contract */
    function withdraw() external onlyOwner returns (bool) {
        uint256 balance = address(this).balance;
        payable(owner()).transfer(balance);
        return true;
    }

    function total() public view returns (uint256) {
        return _nonce.current();
    }

    function openning() public view returns (uint256) {
        return _openning.current();
    }

    /* Returns offerId from openning offerings*/
    function getOpeningOffersId() public view returns (uint256[] memory) {
        uint256 itemCount = _nonce.current();
        uint256 offeringCount = _openning.current();
        uint256 currentIndex = 0;
        uint256[] memory indexes = new uint256[](offeringCount);
        for (uint256 i = 0; i < itemCount; i++) {
            if (!idToOffer[i + 1].closed) {
                indexes[currentIndex] = i + 1;
                currentIndex++;
            }
        }
        return indexes;
    }

    function getOffer(uint256 offerId) public view returns (Offer memory) {
        Offer memory item = idToOffer[offerId];
        return item;
    }

    function getOfferIdBySeller(address seller)
        public
        view
        returns (uint256[] memory)
    {
        uint256 totalOffers = _nonce.current();
        uint256 itemCount = 0;
        uint256 currentIndex = 0;

        for (uint256 i = 0; i < totalOffers; i++) {
            if (idToOffer[i + 1].seller == seller) {
                itemCount++;
            }
        }
        uint256[] memory indexes = new uint256[](itemCount);
        for (uint256 i = 0; i < itemCount; i++) {
            if (idToOffer[i + 1].seller == seller) {
                indexes[currentIndex] = i + 1;
                currentIndex++;
            }
        }
        return indexes;
    }
}
